import { useState, useEffect } from "react";
import {Button} from "react-bootstrap";

const ContentListePoke = (props) => {
  const [ressource, setRessource] = useState("?limit=898");
  const {list, setList} = useState([{count: 3,
    next: "/",
    previous: null,
    results: [{name: "bulbasaur", url: "https://pokeapi.co/api/v2/pokemon/1/"}, {name: "ivysaur", url: "https://pokeapi.co/api/v2/pokemon/2/"}, {name: "venusaur", url: "https://pokeapi.co/api/v2/pokemon/3/"}]}]);
  let i = 0;
  console.log(list);

  const getInfos = async (ressource) => {
    const response = await fetch(
      `https://pokeapi.co/api/v2/pokemon/${ressource}`
    );
    const result = await response.json();
    console.log(result.results);
    result.results.forEach(element => {
      getIndividualInfos(result.results[i].url);
      i++
    });
    return result;
  };

  const getIndividualInfos = async (pokeressource) => {
    const response = await fetch(`${pokeressource}`);
    const resultindividual = await response.json();
    console.log(resultindividual);
    return resultindividual;
  }

  const handleClick = (event, ressource) => {
    event.preventDefault();
    setRessource(ressource);
  };

  useEffect(() => {
    console.log(ressource);
    getInfos(ressource).then((result) => setList(result));
  }, [ressource]);

  return (
    <>
      <div className="container">
        <Button variant="danger" onClick={(event) => handleClick(event, "?limit=898")}>
          All
        </Button>
        <Button variant="danger" onClick={(event) => handleClick(event, "?limit=151")}>
          Kanto
        </Button>
        <Button variant="danger"
          onClick={(event) => handleClick(event, "?limit=100&offset=151")}
        >
          Johto
        </Button>
        <Button variant="danger"
          onClick={(event) => handleClick(event, "?limit=135&offset=251")}
        >
          Hoenn
        </Button>
        <Button variant="danger"
          onClick={(event) => handleClick(event, "?limit=107&offset=386")}
        >
          Sinnoh
        </Button>
        <Button variant="danger"
          onClick={(event) => handleClick(event, "?limit=156&offset=493")}
        >
          Unova
        </Button>
        <Button variant="danger" onClick={(event) => handleClick(event, "?limit=72&offset=649")}>
          Kalos
        </Button>
        <Button variant="danger" onClick={(event) => handleClick(event, "?limit=88&offset=721")}>
          Alola
        </Button>
        <Button variant="danger" onClick={(event) => handleClick(event, "?limit=89&offset=809")}>
          Galar
        </Button>
      </div>
      <div className="containercards">
        {/* {list.results.map((element) => (
          <div className="cardpoke">
            <div className="card-body">
              <h4 className="card-title">
                {resultindividual.order}
                {element.name}
              </h4>
              <p className="card-text">
                Some quick example text to build on the card title and make up
                the bulk of the card's content.
              </p>
            </div>
          </div>
        ))} */}
      </div> 
    </>
  );
};

export default ContentListePoke;
