import { useState, useEffect } from "react";

const Pokecard = (props) => {
  const [pokeinfo, setPokeinfo] = useState([]);

  //get info from ONE pokemon
  const getIndividualInfos = async (pokeressource) => {
    const response = await fetch(`${pokeressource}`);
    const resultindividual = await response.json();
    //console.log(resultindividual);
    setPokeinfo(resultindividual);
    return resultindividual;
  };

  return (
    <>
      <div className="cardpoke">
        <div className="card-body">
          <h4 className="card-title">
            <span></span>
            {props.pokename}
          </h4>
          <p className="card-text">
            Some quick example text to build on the card title and make up the
            bulk of the card's content.
          </p>
        </div>
      </div>
    </>
  );
};

export default Pokecard;
