import { useState, useEffect } from "react";

const GetEvoline = (props) => {
  const url = props.url;
  const [pokeinfo, setPokeinfo] = useState([]);
  const [evoline, setEvoline] = useState([]);

  const getInfos = async (url) => {
    const response = await fetch(url);
    const result = await response.json();
    return result;
  };

  useEffect(() => {
    getInfos(url).then((result) => setPokeinfo(result));
  }, [url]);
  const evoUrl = pokeinfo.evolution_chain.url;

  const getLine = async (evoUrl) => {
    const responseEvo = await fetch(evoUrl);
    const resultEvo = await responseEvo.json();
    return resultEvo;
  }

  useEffect(() => {
    getLine(url).then((resultEvo) => setEvoline(resultEvo));
  }, [evoUrl]);
  console.log(evoline);

  return <></>;
};

export default GetEvoline;
