import { useState, useEffect } from "react";

const ContentListePoke = (props) => {
  const [ressource, setRessource] = useState("all");
  const [list, setList] = useState([]);

  const getInfos = async (ressource) => {
    const response = await fetch(`https://pokeapi.co/api/v2/${ressource}`);
    const result1 = await response.json();
    const result = result1.results;
    return result;
  };

  const handleClick = (event, ressource) => {
    event.preventDefault();
    setRessource(ressource);
  };

  useEffect(() => {
    console.log(ressource);
    getInfos(ressource).then((result) => setList(result));
  }, [ressource]);

  return (
    <>
      <div>
        <button onClick={(event) => handleClick(event, "1")}>
          All
        </button>
        <button onClick={(event) => handleClick(event, "2")}>Kanto</button>
        <button onClick={(event) => handleClick(event, "3")}>
          Johto
        </button>
        <button onClick={(event) => handleClick(event, "hoenn")}>
          Hoenn
        </button>
        <button onClick={(event) => handleClick(event, "sinnoh")}>
          Sinnoh
        </button>
        <button onClick={(event) => handleClick(event, "unova")}>
          Unova
        </button>
        <button onClick={(event) => handleClick(event, "kalos")}>
          Kalos
        </button>
        <button onClick={(event) => handleClick(event, "alola")}>
          Alola
        </button>
        <button onClick={(event) => handleClick(event, "galar")}>
          Galar
        </button>
      </div>
      <div>
        <ul>
          {list.map((element) => (
            <li>{element.name}</li>
          ))}
        </ul>
      </div>
    </>
  );
};

export default ContentListePoke;
