  let geturl = `https://pokeapi.co/api/v2/pokemon/${nb}`;

  const getInfos = async (geturl) => {
    const response = await fetch(geturl);
    const result = await response.json();
    console.log(result);
    return result;
  };

  useEffect(() => {
    getInfos(geturl).then((result) => setPokeinfo(result));
  }, [url]);

