const ContentListItem = (props) => {
  const getItems = async () => {
    const response = await fetch(`https://pokeapi.co/api/v2/item-category/?limit=46`);
    const result = await response.json();
    console.log(result);
    return result;
  };

  const handleClick = (event, ressource) => {
    event.preventDefault();
    setRessource(ressource);
  };

  useEffect(() => {
    console.log(ressource);
    getInfos(ressource).then((result) => setList(result));
  }, [ressource]);

  getItems();

  return (
    <>
      <div className="container">
        <Button
          variant="danger"
          onClick={(event) => handleClick(event, "1")}
        >
          Stat boosts
        </Button>
        <Button
          variant="danger"
          onClick={(event) => handleClick(event, "2")}
        >
          Effort drop
        </Button>
        <Button
          variant="danger"
          onClick={(event) => handleClick(event, "3")}
        >
          Medecine
        </Button>
        <Button
          variant="danger"
          onClick={(event) => handleClick(event, "4")}
        >
          Other
        </Button>
        <Button
          variant="danger"
          onClick={(event) => handleClick(event, "5")}
        >
          In a pinch
        </Button>
        <Button
          variant="danger"
          onClick={(event) => handleClick(event, "6")}
        >
          Picky healing
        </Button>
        <Button
          variant="danger"
          onClick={(event) => handleClick(event, "7")}
        >
          Type protection
        </Button>
        <Button
          variant="danger"
          onClick={(event) => handleClick(event, "8")}
        >
          Baking only
        </Button>
        <Button
          variant="danger"
          onClick={(event) => handleClick(event, "9")}
        >
          Collectibles
        </Button>
        <Button
          variant="danger"
          onClick={(event) => handleClick(event, "10")}
        >
          Evolution
        </Button>
        <Button
          variant="danger"
          onClick={(event) => handleClick(event, "11")}
        >
          Spelunking
        </Button>
        <Button
          variant="danger"
          onClick={(event) => handleClick(event, "12")}
        >
          Held items
        </Button>
        <Button
          variant="danger"
          onClick={(event) => handleClick(event, "13")}
        >
          Choice
        </Button>
        <Button
          variant="danger"
          onClick={(event) => handleClick(event, "14")}
        >
          Effort training
        </Button>
        <Button
          variant="danger"
          onClick={(event) => handleClick(event, "15")}
        >
          Bad held items
        </Button>
        <Button
          variant="danger"
          onClick={(event) => handleClick(event, "16")}
        >
          Training
        </Button>
        <Button
          variant="danger"
          onClick={(event) => handleClick(event, "17")}
        >
          Plates
        </Button>
        <Button
          variant="danger"
          onClick={(event) => handleClick(event, "18")}
        >
          Species specific
        </Button>
        <Button
          variant="danger"
          onClick={(event) => handleClick(event, "19")}
        >
          Type enhancement
        </Button>
        <Button
          variant="danger"
          onClick={(event) => handleClick(event, "20")}
        >
          Event items
        </Button>
      </div>
    </>
  );
};

export default ContentListItem;
