import { useState, useEffect } from "react";
import ReactTooltip from "react-tooltip";

const EgglistPokecard = (props) => {
  let name = props.pokename;
  const [pokeinfo, setPokeinfo] = useState([]);
  let url = `https://pokeapi.co/api/v2/pokemon/${name}`;
  let tooltipname = "";

  const getInfos = async (url) => {
    const response = await fetch(url);
    const result = await response.json();
    console.log(result.name);
    return result;
  };

  useEffect(() => {
    getInfos(url).then((result) => setPokeinfo(result));
  }, [url]);

  const getTooltip = () => {
      if (pokeinfo.name) {
        tooltipname = pokeinfo.name;
      } else return;
      return tooltipname;
  }

  return (
    <>
      {pokeinfo.sprites ? (
        <div>
          <button data-tip data-for="pokename">
            <img
              data-tip
              data-for="pokename"
              className="pokeimg"
              src={pokeinfo.sprites.front_default}
            />
          </button>

          <ReactTooltip id="pokename" place="top" effect="solid">
            {tooltipname}
          </ReactTooltip>
        </div>
      ) : (
        <img className="pokeimg" src="/Assets/img/image0.png" />
      )}
    </>
  );
};

export default EgglistPokecard;
