import { useState, useEffect } from "react";

const ContentListePoke = (props) => {
  const [ressource, setRessource] = useState("");
  const [list, setList] = useState([]);

  const getInfos = async (ressource) => {
    const response = await fetch(
      `https://pokeapi.co/api/v2/pokemon/${ressource}`
    );
    const result = await response.json();
    console.log(result.results);
    return result;
  };

  const handleClick = (event, ressource) => {
    event.preventDefault();
    setRessource(ressource);
  };

  useEffect(() => {
    console.log(ressource);
    getInfos(ressource).then((result) => setList(result));
  }, [ressource]);

  return (
    <>
      <div>
        <button onClick={(event) => handleClick(event, "?limit=898")}>All</button>
        <button onClick={(event) => handleClick(event, "?limit=151")}>Kanto</button>
        <button onClick={(event) => handleClick(event, "?limit=100&offset=151")}>Johto</button>
        <button onClick={(event) => handleClick(event, "?limit=135&offset=251")}>Hoenn</button>
        <button onClick={(event) => handleClick(event, "?limit=107&offset=386")}>
          Sinnoh
        </button>
        <button onClick={(event) => handleClick(event, "?limit=156&offset=493")}>Unova</button>
        <button onClick={(event) => handleClick(event, "?limit=72&offset=649")}>Kalos</button>
        <button onClick={(event) => handleClick(event, "?limit=88&offset=721")}>Alola</button>
        <button onClick={(event) => handleClick(event, "?limit=89&offset=809")}>Galar</button>
      </div>
      {/* <div>
        <ul>
          {list.results.map((element) => (
            <li>{element.name}</li>
          ))}
        </ul>
      </div> */}
      <div>
      <div class="card border-primary mb-3" style="max-width: 20rem;">
  <div class="card-header">Header</div>
  <div class="card-body">
    <h4 class="card-title">Primary card title</h4>
    <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
  </div>
</div>
      </div>
    </>
  );
};

export default ContentListePoke;
