  //   const responseEvo = await fetch(evoUrl);
  //   const resultEvo = await responseEvo.json();
  //   return resultEvo;
  // }

  // useEffect(() => {
  //   getLine(url).then((resultEvo) => setEvoline(resultEvo));
  // }, [evoUrl]);
  // console.log(evoline);

  return (
    <>
      {pokeinfo && pokeinfo.evolution_chain ? <ShowEvoline url={pokeinfo.evolution_chain.url} /> : <p></p> }

    </>
  );
};

export default GetEvoline;
