import { useState, useEffect } from "react";

const ContentListePoke = (props) => {
  const [ressource, setRessource] = useState("berry");
  const [list, setList] = useState([]);

  const getInfos = async (ressource) => {
    const response = await fetch(`https://pokeapi.co/api/v2/${ressource}`);
    const result1 = await response.json();
    const result = result1.results;
    return result;
  };

  const handleClick = (event, ressource) => {
    event.preventDefault();
    setRessource(ressource);
  };

  useEffect(() => {
    console.log(ressource);
    getInfos(ressource).then((result) => setList(result));
  }, [ressource]);

  return (
    <>
      <div>
        <button onClick={(event) => handleClick(event, "berry")}>
          Berries
        </button>
        <button onClick={(event) => handleClick(event, "type")}>Types</button>
        <button onClick={(event) => handleClick(event, "ability")}>
          Abilities
        </button>
      </div>
      <div>
        <ul>
          {list.map((element) => (
            <li>{element.name}</li>
          ))}
        </ul>
      </div>
    </>
  );
};

export default ContentListePoke;
