import { useState, useEffect } from "react";
import ReactTooltip from "react-tooltip";

const EgglistPokecard = (props) => {
  let name = props.pokename;
  const [pokeinfo, setPokeinfo] = useState([]);
  let url = `https://pokeapi.co/api/v2/pokemon/${name}`;

  const getInfos = async (url) => {
    const response = await fetch(url);
    const result = await response.json();
    console.log(result);
    return result;
  };

  useEffect(() => {
    getInfos(url).then((result) => setPokeinfo(result));
  }, [url]);

  return (
    <>
      {pokeinfo.sprites ? (
        <div>
<button data-tip data-for="registerTip">
        Register
      </button>

      <ReactTooltip id="registerTip" place="top" effect="solid">
        {pokeinfo.name}
      </ReactTooltip>
          <img data-tip data-for="pokename" className="pokeimg" src={pokeinfo.sprites.front_default} />
        </div>
      ) : (
        <img className="pokeimg" src="/Assets/img/image0.png" />
      )}
    </>
  );
};

export default EgglistPokecard;
